import { getSupabaseServer } from "./supabaseServer";

export interface AIConfig {
  id: string;
  stage: string;
  model: string;
  system_prompt: string;
  user_prompt_template: string;
  // Mode-specific templates for ideate stage (optional; falls back to user_prompt_template)
  user_prompt_template_idea?: string;
  user_prompt_template_problem?: string;
  user_prompt_template_review?: string;
  user_prompt_template_initial_feedback?: string;
  // Mode-specific system prompts for ideate stage (optional; falls back to system_prompt)
  system_prompt_idea?: string;
  system_prompt_problem?: string;
  system_prompt_surprise?: string;
  system_prompt_review?: string;
  system_prompt_initial_feedback?: string;
  // Mode-specific system prompts for build stage (optional; falls back to system_prompt)
  system_prompt_vibe_coder?: string;
  system_prompt_send_to_devs?: string;
  // Design stage subsection prompts (optional; falls back to system_prompt)
  system_prompt_product_blueprint?: string;
  system_prompt_user_personas?: string;
  system_prompt_user_journey?: string;
  system_prompt_information_architecture?: string;
  system_prompt_wireframes?: string;
  system_prompt_brand_identity?: string;
  system_prompt_mvp_definition?: string;
  // Launch stage subsection prompts (optional; falls back to system_prompt)
  system_prompt_launch_overview?: string;
  system_prompt_launch_strategy?: string;
  system_prompt_launch_messaging?: string;
  system_prompt_launch_landing?: string;
  system_prompt_launch_adopters?: string;
  system_prompt_launch_assets?: string;
  system_prompt_launch_metrics?: string;
  system_prompt_launch_pack?: string;
  // Build/Monetise extra helpers
  system_prompt_scope_suggest?: string;
  // Monetise stage subsection prompts (optional; falls back to system_prompt)
  system_prompt_monetise_overview?: string;
  system_prompt_monetise_pricing?: string;
  system_prompt_monetise_offer?: string;
  system_prompt_monetise_checkout?: string;
  system_prompt_monetise_activation?: string;
  system_prompt_monetise_assets?: string;
  system_prompt_monetise_pack?: string;
  created_at: string;
  updated_at: string;
}

export async function getAIConfig(stage: string): Promise<AIConfig | null> {
  try {
    const supabase = getSupabaseServer();
    const { data, error } = await supabase
      .from("ai_configs")
      .select("*")
      .eq("stage", stage)
      .single();

    if (error) {
      console.error(`Error fetching AI config for ${stage}:`, error);
      return null;
    }

    return data;
  } catch (error) {
    console.error(`Exception fetching AI config for ${stage}:`, error);
    return null;
  }
}

export function substitutePromptTemplate(template: string, variables: Record<string, string>): string {
  let result = template;
  
  // Replace all ${variable} placeholders with actual values
  Object.entries(variables).forEach(([key, value]) => {
    // Escape special regex characters in the placeholder
    const placeholder = `\\$\\{${key}\\}`;
    result = result.replace(new RegExp(placeholder, 'g'), value || '');
  });
  
  return result;
}

